<template>
  <div>
		<template v-if="menuType==='slide'||menuType==='all'">
			<div class="slider-menu prev" :class="{disabled: initSlide === 0}" @click="onMove(-1)"></div>
			<div class="slider-menu next" :class="{disabled: initSlide === itemsLength - 1}" @click="onMove(1)"></div>
		</template>
		<div class="toolbar-wrapper" :style="{bottom:`${thumbnailHeight + 20}px`}" v-if="menuType==='bottom'||menuType==='all'">
			<ul class="toolbar">
				<li class="zoom-in" @click="onScale(-1)"></li>
				<li class="zoom-out" @click="onScale(1)"></li>
				<li class="prev" @click="onMove(-1)"></li>
				<li class="next" @click="onMove(1)"></li>
				<li class="rotate-left" @click="onRotate(90)"></li>
				<li class="rotate-right" @click="onRotate(-90)"></li>
			</ul>
		</div>
  </div>
</template>

<script>
export default {
	props: {
		initSlide: {
			type: Number
		},
		itemsLength: {
			type: Number,
			default: 0
		},
		thumbnailHeight: {
			type: Number
		},
		menuType: {
			type: String
		}
	},
	methods: {
		onMove(val) {
			this.$emit("onMove", val);
		},
		onScale(val) {
			this.$emit("onScale", val);
		},
		onRotate(val) {
			this.$emit("onRotate", val);
		}
	}
};
</script>

<style scoped lang="scss">
.slider-menu {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	background-color: rgba(0, 0, 0, 0.3);
	position: absolute;
	top: 50%;
	z-index: 9999;
	margin-top: -40px;
	transition: 0.2s;
	cursor: pointer;
	&.disabled {
		opacity: 0.2;
		cursor: not-allowed;
		&:hover {
			background-color: rgba(0, 0, 0, 0.3);
		}
	}
	&:hover {
		background-color: rgba(0, 0, 0, 0.8);
	}
	&.prev {
		left: 20px;
		&:before {
			content: "";
			margin: 16px 12px;
			font-size: 0;
			line-height: 0;
			display: block;
			width: 48px;
			height: 48px;
			color: transparent;
			background: url("../../icons/left.png");
		}
	}
	&.next {
		right: 20px;
		&:before {
			content: "";
			margin: 16px 0px 16px 20px;
			font-size: 0;
			line-height: 0;
			display: block;
			width: 48px;
			height: 48px;
			color: transparent;
			background: url("../../icons/right.png");
		}
	}
}
.toolbar-wrapper {
	position: fixed;
	left: 0;
	right: 0;
	z-index: 9999;
	display: flex;
	justify-content: center;
	.toolbar {
		display: flex;
		justify-content: center;
		li {
			color: #fff;
			margin: 0 2px;
			list-style: none;
			width: 24px;
			height: 24px;
			cursor: pointer;
			border-radius: 50%;
			transition: 0.2s;
			background-repeat: no-repeat;
			background-color: rgba(0, 0, 0, 0.5);
			&:hover {
				background-color: rgba(0, 0, 0, 0.9);
			}
			&:before {
				margin: 2px;
				font-size: 0;
				line-height: 0;
				display: block;
				width: 20px;
				height: 20px;
				color: transparent;
				background-image: url(data:image/png;base64,);
				background-repeat: no-repeat;
			}
			&.zoom-in {
				&:before {
					content: "Zoom In";
					background-position: 0 0;
				}
			}
			&.zoom-out {
				&:before {
					content: "Zoom Out";
					background-position: -20px 0;
				}
			}
			&.prev {
				&:before {
					content: "Prev";
					background-position: -80px 0;
				}
			}
			&.next {
				&:before {
					content: "Next";
					background-position: -120px 0;
				}
			}
			&.rotate-left {
				&:before {
					content: "Rotate Left";
					background-position: -140px 0;
				}
			}
			&.rotate-right {
				&:before {
					content: "Rotate right";
					background-position: -160px 0;
				}
			}
		}
	}
}
</style>
